import React, { Component } from "react";
import { Row, Col } from "antd";
import Radium, { StyleRoot } from "radium";
import { fadeInDown, fadeInUp } from "react-animations";

import "../styles/MovieDetail.css";

// styles component
import { Spinner } from "react-bootstrap";

// apis
import { getMovieById } from "../apis/movieApis";

const styles = {
  fadeInDown: {
    animation: "x 1s",
    animationName: Radium.keyframes(fadeInDown, "fadeInDown"),
    width: 195
  },
  fadeInUp: {
    animation: "x 2.5s",
    animationName: Radium.keyframes(fadeInUp, "fadeInUp")
  }
};
export default class MovieDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      movie: null
    };
    this.isComponentMounted = true;
  }

  static async getInitialProps({ query }) {
    return { query };
  }
  async componentDidMount() {
    const movieId = this.props.query.id;
    await this.fetchMovie(movieId);
  }

  fetchMovie = async id => {
    try {
      this.setState({ loading: true });
      const response = await getMovieById({ id });
      this.setState({ movie: response });
    } catch (err) {
      console.log(err);
    } finally {
      if (this.isComponentMounted) this.setState({ loading: false });
    }
  };

  render() {
    if (this.state.loading || !this.state.movie) {
      return (
        <div
          style={{
            display: "flex",
            flex: 1,
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%"
          }}
        >
          <Spinner animation="border" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>
        </div>
      );
    }

    const {
      backdrop_path,
      original_title,
      original_language,
      release_date,
      overview,
      vote_count,
      vote_average,
      popularity,
      adult
    } = this.state.movie;

    return (
      <StyleRoot>
        <div className="imagebg">
          <Row className="p-5">
            <Col
              offset={2}
              lg={4}
              xs={24}
              sm={24}
              className="d-flex justify-content-center align-items-center"
            >
              <img
                style={styles.fadeInDown}
                className="mb-3 img"
                alt="example"
                src={`https://image.tmdb.org/t/p/w500${backdrop_path}`}
              />
            </Col>
            <div style={styles.fadeInUp}>
              <Col offset={1} lg={14} xs={24} sm={24}>
                <Row>
                  <Col>
                    <h4
                      style={{
                        float: "left",
                        color: "#fff",
                        fontWeight: 800
                      }}
                    >
                      {original_title}
                    </h4>
                  </Col>
                </Row>
                <Row>
                  <Col lg={8} xs={24} sm={24}>
                    <p className="heading">
                      Popularity&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :{" "}
                      <span className="headingValue">{popularity}</span>
                    </p>
                  </Col>
                  <Col lg={8} xs={24} sm={24}>
                    <p className="heading">
                      Vote Count &nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                      <span className="headingValue">{vote_count}</span>
                    </p>
                  </Col>
                  <Col lg={8} xs={24} sm={24}>
                    <p className="heading">
                      Vote Average :{" "}
                      <span className="headingValue">
                        {vote_average} <span style={{ fontSize: 12 }}>/10</span>{" "}
                      </span>
                    </p>
                  </Col>
                </Row>
                <Row>
                  <Col lg={22}>
                    <hr
                      style={{
                        color: "#a6a6a6",
                        backgroundColor: "#a6a6a6",
                        height: 1
                      }}
                    />
                  </Col>
                </Row>
                <Row>
                  <Col lg={12} xs={24} sm={24}>
                    <p className="headingValue" style={{ color: "white" }}>
                      Original Language &nbsp;&nbsp;&nbsp;:
                      <span style={{ color: "#570AFF" }}>
                        {" "}
                        &nbsp;&nbsp;{original_language}
                      </span>
                    </p>
                  </Col>
                </Row>
                <Row>
                  <Col lg={12} xs={24} sm={24}>
                    <p className="headingValue" style={{ color: "white" }}>
                      Date of Release
                      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                      <span style={{ color: "#570AFF" }}>
                        {" "}
                        &nbsp;&nbsp;{release_date}
                      </span>
                    </p>
                  </Col>
                </Row>
                <Row>
                  <Col lg={12} xs={24} sm={24}>
                    <p className="headingValue" style={{ color: "white" }}>
                      Adult
                      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:{" "}
                      <span style={{ color: "#570AFF" }}>
                        {" "}
                        &nbsp;&nbsp;{adult ? "YES" : "NO"}
                      </span>
                    </p>
                  </Col>
                </Row>
                <Row>
                  <Col lg={24} xs={24} sm={24}>
                    <h5
                      className="headingValue"
                      style={{ color: "#570AFF", fontSize: 16 }}
                    >
                      Description
                    </h5>

                    <p className="heading">{overview}</p>
                  </Col>
                </Row>
              </Col>
            </div>
          </Row>
        </div>
      </StyleRoot>
    );
  }
}
