import React from "react";
import Header from "../layouts/Header";

// style
import "bootstrap/dist/css/bootstrap.min.css";
import "antd/dist/antd.css";

// styled component
import { Container, Row, Col } from "react-bootstrap";
import { Descriptions, Card } from "antd";

// apis
import { getMovieById } from "../apis/movieApis";

export default class Detail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      movie: null
    };
    this.isComponentMounted = true;
  }
  static async getInitialProps({ query }) {
    return { query };
  }

  async componentDidMount() {
    const movieId = this.props.query.id;
    await this.fetchMovie(movieId);
  }

  fetchMovie = async id => {
    try {
      this.setState({ loading: true });
      const response = await getMovieById({ id });
      this.setState({ movie: response });
    } catch (err) {
      console.log(err);
    } finally {
      if (this.isComponentMounted) this.setState({ loading: false });
    }
  };

  render() {
    return (
      <div>
        <Header search={false} />
        <Container style={{ marginTop: 70 }}>
          <Card>
            <Descriptions title="User Info" bordered={false} layout="vertical">
              <Descriptions.Item label="UserName">
                Zhou Maomao
              </Descriptions.Item>
              <Descriptions.Item label="Telephone">
                1810000000
              </Descriptions.Item>
              <Descriptions.Item label="Live">
                Hangzhou, Zhejiang
              </Descriptions.Item>
              <Descriptions.Item label="Address" span={2}>
                No. 18, Wantang Road, Xihu District, Hangzhou, Zhejiang, China
              </Descriptions.Item>
              <Descriptions.Item label="Remark">empty</Descriptions.Item>
            </Descriptions>
          </Card>
        </Container>
      </div>
    );
  }
}
